mapper.insert:

INSERT
    INTO ABC_PRODUCT (
        product_id,
        product_name
    )
    VALUES (
        #{productID},
        #{productName}
    )

mapper.update:

UPDATE ABC_PRODUCT
SET product_name = #{productName}
WHERE product_id = #{productID}

mapper.select:
SELECT * FROM (
SELECT ROW_NUMBER() OVER (ORDER BY product_id DESC) as rnum, product_id as productID, product_name as productName FROM ABC_PRODUCT
AND product_id = #{productID}
AND UPPER(product_name) LIKE CONCAT('%', UPPER(#{productName}), '%')
) as product
AND product.rnum BETWEEN (#{pageSize} * (#{pageNumber} - 1) + 1) AND (#{pageSize} * #{pageNumber})

SELECT COUNT(*) FROM ABC_PRODUCT 


        AND product_id = #{productID}


        AND UPPER(product_name) LIKE CONCAT('%', UPPER(#{productName}), '%')
